document.addEventListener("DOMContentLoaded",()=>{const s=document.getElementById("toggle"),e=document.getElementById("sidebar");window.innerWidth>900?(e.classList.add("open"),s.classList.add("open")):(e.classList.add("close"),s.classList.add("close")),window.addEventListener("resize",()=>{window.innerWidth>900?(e.classList.remove("close"),e.classList.add("open"),s.classList.remove("close"),s.classList.add("open")):(e.classList.remove("open"),e.classList.add("close"),s.classList.remove("open"),s.classList.add("close"))}),document.addEventListener("keydown",o=>{"Escape"===o.key&&e.classList.contains("open")&&(e.classList.remove("open"),e.classList.add("close"),s.classList.remove("open"),s.classList.add("close"))}),s.addEventListener("click",()=>{e.classList.contains("open")?(e.classList.remove("open"),e.classList.add("close"),s.classList.remove("open"),s.classList.add("close")):(e.classList.remove("close"),e.classList.add("open"),s.classList.remove("close"),s.classList.add("open"))}),$(window).scroll(function(){0!=$(this).scrollTop()?$("#gotop").fadeIn():$("#gotop").fadeOut()}),$("#gotop").on("click",function(s){$("body,html").animate({scrollTop:0},800),s.preventDefault()})});